@use '../abstracts/mixins' as *;
@use '../abstracts/font-face' as *;

button,
.base-btn {
  cursor: pointer;
  padding: 1em;
  box-shadow: 0px 0px 0px transparent;
  border: 0px solid transparent;
  text-shadow: 0px 0px 0px transparent;
  background-color: transparent;
}

.primary,
.secondary,
.submit {
  font-size: var(--fs-200);
  text-transform: uppercase;
  letter-spacing: 1px;
  border-radius: 0.3em;
  width: 100%;
}

.primary {
  background-color: var(--clr-accent-light-blue);
  margin-right: 1em;
  transition: all ease-in-out 250ms;
  color: var(--clr-neutral-100);
  @include mq(medium) {
    &:hover {
      background-color: var(--clr-accent-blue);
      transform: translateY(-2px);
      color: var(--clr-neutral-100);
      font-weight: $fw-b;
    }
  }
}
.secondary {
  transition: all ease-in-out 250ms;
  border: solid 2px transparent;
  color: var(--clr-neutral-600);
  @include mq(medium) {
    &:hover {
      border: solid 2px var(--clr-accent-orange);
      transform: translateY(-2px);
      color: var(--clr-accent-orange);
    }
  }
}
.submit {
  color: var(--clr-neutral-100);
  border: 2px solid var(--clr-neutral-100);
  transition: all ease-in-out 250ms;
  @include mq(medium) {
    &:hover {
      background-color: var(--clr-neutral-100);
      color: var(--clr-accent-light-blue);
    }
  }
}

sources.scss/components/_nav.scss:
@use "../abstracts/mixins" as *;

#kg-logo svg {
  height: 50px;
  width: 50px;
  path,
  g {
    fill: var(--clr-neutral-600);
  }
}

#theme-toggle {
  height: 40px;
  width: 40px;
  padding: 0;
  border-radius: 0.25em;
  transition: all ease-in-out 250ms;
  @include mq(medium) {
    &:hover {
      background-color: var(--clr-neutral-200);
    }
  }
}

nav {
  padding: 1em 0;
  position: sticky;
  top: 0;
  z-index: 9;
  background-color: var(--clr-neutral-100);
  .container {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  }
  #middle {
    display: none;
  }
  @include mq(medium) {
    #middle {
      display: flex;
      li {
        margin: 0 0.25em;
        &:hover {
          .nav-link {
            box-shadow: 4px 4px var(--clr-accent-light-blue);
            border: solid 4px var(--clr-accent-light-blue);
            transform: translate(-4px, -4px);
            color: var(--clr-accent-light-blue);
            transition: all ease-in-out 250ms;
          }
        }
        .nav-link {
          text-align: center;
          height: 100%;
          width: 100%;
          background-color: var(--clr-neutral-100);
          border: solid 4px transparent;
          border-radius: 0.25em;
          padding: 0.25em 1em;
          color: var(--clr-neutral-600);
          transition: all ease-in-out 250ms;
        }
      }
    }
  }
}

// ---------------------------------
// Establishing Mobile Functionality
// ---------------------------------

#mobile-menu {
  flex-direction: column;
  align-items: center;
  position: relative;
  z-index: 11;

  transform: translateY(-10px);
  transition: transform ease-in-out 250ms;

  #arrow-up,
  #arrow-down {
    height: 12px;
    width: 12px;
  }
  #arrow-up {
    fill-opacity: 0;
    transition: fill-opacity ease-in-out 250ms;
  }
  button {
    padding: 0;
  }
  @include mq(medium) {
    display: none;
  }
}

#nav-link-wrapper-mobile {
  flex-direction: column;
  justify-content: space-around;
  padding: 7em 2em;
  margin: 0;
  height: 100vh;
  width: 100%;
  border: solid 0.8em var(--clr-accent-light-blue);
  background-color: var(--clr-neutral-100);
  position: absolute;
  top: 0;
  z-index: 10;

  transform: translateY(-100%);
  transition: transform ease-in-out 250ms;
  @include mq(medium) {
    display: none;
  }
}

.nav-is-open {
  #nav-link-wrapper-mobile {
    transform: translateY(0%);
  }
  #mobile-menu {
    button {
      color: var(--clr-accent-light-blue);
    }

    transform: translateY(10px);

    #arrow-up {
      fill-opacity: 100;
      fill: var(--clr-accent-light-blue);
    }
    #arrow-down {
      fill-opacity: 0;
      transition: fill-opacity ease-in-out 250ms;
    }
  }
}

.social-links {
  a {
    margin: 0 0.15em;
    svg,
    g,
    path {
      fill: var(--clr-accent-light-blue);
    }
    @include mq(medium) {
      &:hover {
        svg,
        g,
        path {
          fill: var(--clr-accent-orange);
        }
      }
    }
  }
}

