    font-size: var(--fs-600);
    line-height: 1.25;
    margin-bottom: 0.5em;
  }
  .project-intro-details {
    padding: 1em;
    border: solid 3px var(--clr-neutral-600);
    border-radius: 0.5em;
    margin: 0 auto 4em;

    li {
      display: flex;
      flex-direction: column;
      margin-bottom: 2em;
      &:last-child {
        margin-bottom: 0;
      }
      .project-details-h3 {
        font-size: var(--fs-200);
        text-transform: uppercase;
        margin-bottom: 0.25em;
      }
      span {
        font-size: var(--fs-300);
      }
    }
  }
  // -----------------------------
