  // -----------------------------
  @include mq(medium) {
    flex-direction: row-reverse;
    justify-content: space-between;
    .project-description {
      width: 50%;
      margin: 0 auto;
    }
    .project-intro-details {
      display: flex;
      flex-direction: column;
      margin: 0 auto;
      li {
        display: flex;
      }
    }
  }
}

// -----------------------------
//  Project Content Block
// -----------------------------
.project-content-block {
  background-color: var(--clr-neutral-200);
  padding-top: 1em;
  padding-bottom: 1em;
  h2 {
    margin-bottom: 0.5em;
  }
  // -----------------------------
  //  Project Content Block (Desktop)
  // -----------------------------
  @include mq(medium) {
    .container {
      display: flex;
      padding-top: 4em;
      padding-bottom: 4em;
      h2 {
        margin: 0 1em 0 0;
        width: 100%;
      }
    }
  }
}

