sources.scss/main.scss:
// change namespace by using "as"
// @use '...' as newName
@use 'base';
@use 'utilities';
@use 'components';
@use 'layout';
@use 'abstracts';
@use 'pages';
@use 'src/scss/abstracts/colors' as *;
@use "src/scss/abstracts/type-scale" as *;
@use "src/scss/abstracts/mixins" as *;

:root {
  @each $color, $shades in $colors {
    @each $shade, $value in $shades {
      --clr-#{$color}-#{""+$shade}: #{$value};
    }
  }

  @each $screen-size, $font-size in $type-scale {
    @if $screen-size == small {
      @each $size, $value in $font-size {
        --fs-#{$size}: #{$value};
      }
    } @else {
      @include mq(medium) {
        @each $size, $value in $font-size {
          --fs-#{$size}: #{$value};
        }
      }
    }
  }
}

body {
  position: relative;
}
